mapper.insert:
INSERT INTO user (
    username, user_account, user_password, gender, 
    phone, email, avatar_url, user_profile, user_role, is_delete
) VALUES (
    #{username}, #{userAccount}, #{userPassword}, #{gender}, 
    #{phone}, #{email}, #{avatarUrl}, #{userProfile}, #{userRole}, #{isDelete}
)

mapper.update:
UPDATE user
username = #{username}, user_password = #{userPassword}, gender = #{gender}, phone = #{phone}, email = #{email}, avatar_url = #{avatarUrl}, user_role = #{userRole}, is_delete = 0, update_time = CURRENT_TIMESTAMP
WHERE id = #{id}

mapper.select:
SELECT FROM user WHERE is_delete = 0
AND username LIKE CONCAT('%', #{username}, '%')
ORDER BY create_time DESC LIMIT #{offset}, #{size}

